import React, { Component } from "react";
import {
  Platform,
  StyleSheet,
  View,
  Animated,
  Easing,
  TouchableWithoutFeedback,
  ActivityIndicator,
  ToastAndroid,
  Alert
} from "react-native";
import { Snackbar, Title, Button, Text } from "react-native-paper";
import { MapView, Location, Permissions } from "expo";
import { connect } from "react-redux";
import isArray from "lodash/isArray";
import has from "lodash/has";
import moment from "moment";
import PropTypes from "prop-types";

import Layout from "../constants/Layout";
import MarkerLang from "../languages/MarkerLang";
import GlobalLang from "../languages/GlobalLang";
import SearchLocationButton from "../components/MarkerScreen/SearchLocationButton";
import FormMarker from "../components/MarkerScreen/FormMarker";
import APIMaps from "../constants/APIMaps";
import { submitForm } from "../ducks/ListMarkerDuck";

const {
  window: { height }
} = Layout;

class MarkerScreen extends Component {
  static CHANGE_CURRENT_LOCATION = "CHANGE_CURRENT_LOCATION";

  state = {
    form: {
      useReminder: false,
      reminderAt: {
        date: moment().format("DD/MM/YYYY"),
        time: moment().format("H:mm")
      },
      images: [
        {
          id: 123456789,
          image: ``
        }
      ],
      maps: {
        address: "",
        latitude: -6.1753924,
        longitude: 106.8249641
      }
    },
    loading: false,
    getCurrentLocation: false
  };

  constructor() {
    super();
    this.initialRegion = {
      latitude: -6.1753924,
      longitude: 106.8249641
    };
    this.delta = {
      latitudeDelta: 0.0052,
      longitudeDelta: 0.0052
    };
    this.floatingForm = new Animated.Value(0);
  }

  /* when component already render then run this function */
  componentDidMount() {
    this.getDetailCurrentLocation();
  }

  /* when first load this component run this function */
  getDetailCurrentLocation = async () => {
    try {
      const currentLocation = await this.getCurrentLocation();
      if (has(currentLocation, "coords")) {
        const { latitude, longitude } = currentLocation.coords;

        // get current address depend on longitude latitude
        const mAPIMaps = new APIMaps();
        const result = await mAPIMaps.getAddressFromLatLong(
          latitude,
          longitude
        );

        // get address from fetching data
        let address = "";
        if (has(result, "results")) {
          let i = 0;
          while (address === "") {
            if (
              has(result.results[i], "formatted_address") &&
              typeof result.results[i] !== "undefined" &&
              result.results[i].formatted_address !== ""
            ) {
              address = result.results[i].formatted_address;
            }
            i++;
          }
        }

        // set the state
        this.setState({
          ...this.state,
          form: {
            ...this.state.form,
            maps: {
              address,
              latitude: Number(latitude),
              longitude: Number(longitude)
            }
          },
          getCurrentLocation: false
        });
      } else {
        throw new Error(MarkerLang.failedGetCurrentLocation);
      }
    } catch (e) {
      Alert.alert(
        GlobalLang.failed,
        isArray(e) ? JSON.stringify(e) : e.toString()
      );
    }
  };

  /* check permission google maps, if true then get location */
  getCurrentLocation = async () => {
    const { status } = await Permissions.askAsync(Permissions.LOCATION);
    if (status === "granted") {
      return Location.getCurrentPositionAsync({ enableHighAccuracy: true });
    } else {
      throw new Error(GlobalLang.permissionMapsDenied);
    }
  };

  /* toggle form animation */
  toggleForm = () => {
    Animated.timing(this.floatingForm, {
      toValue: this.floatingForm["_value"] === 1 ? 0 : 1,
      duration: 800,
      easing: Easing.linear
    }).start();
  };

  /* navigate to search location screen */
  navigateToSearchLocation = () => {
    this.props.navigation.navigate("SearchLocation", {
      [MarkerScreen.CHANGE_CURRENT_LOCATION]: this.onChangeStateMaps
    });
  };

  /* callback for update the state */
  onChangeTextForm = (stateName1, stateName2 = "", value) => {
    if (stateName2 === "") {
      this.setState({
        ...this.state,
        form: {
          ...this.state.form,
          [stateName1]: value
        }
      });
    } else {
      this.setState({
        ...this.state,
        form: {
          ...this.state.form,
          [stateName1]: {
            ...this.state.form[stateName1],
            [stateName2]: value
          }
        }
      });
    }
  };

  /* callback add image */
  onImageAdd = imageBase64 => {
    let { images } = this.state.form;
    this.setState({
      ...this.state,
      form: {
        ...this.state.form,
        images: [...images, imageBase64]
      }
    });
  };

  /* callback remove image */
  onRemoveImage = idImage => {
    let { images } = this.state.form;
    images = images.filter(({ id }) => id !== idImage);
    this.setState({
      ...this.state,
      form: {
        ...this.state.form,
        images
      }
    });
  };

  /* submit form */
  onSubmit = () => {
    this.setState(
      {
        ...this.state,
        loading: true
      },
      () => {
        this.props.submitForm(
          this.state.form,
          this.successSubmitForm,
          this.failedSubmitForm
        );
      }
    );
  };

  /* callback when success submit form */
  successSubmitForm = () => {
    this.setState({
      ...this.state,
      form: {
        ...this.state.form,
        name: "",
        address: "",
        useReminder: false,
        reminderAt: {
          date: moment().format("DD/MM/YYYY"),
          time: moment().format("H:mm")
        },
        images: []
      },
      loading: false
    });
    ToastAndroid.show(MarkerLang.successSubmitForm, ToastAndroid.SHORT);
  };

  failedSubmitForm = () => {
    this.setState({
      ...this.state,
      loading: false
    });
    Alert.alert(GlobalLang.failed, MarkerLang.failedSubmitForm);
  };

  /* change maps location */
  onChangeStateMaps = data => {
    this.setState({
      ...this.state,
      form: {
        ...this.state.form,
        maps: {
          ...this.state.maps,
          ...data
        }
      }
    });
  };

  /* render ui */
  render() {
    const bottomForm = this.floatingForm.interpolate({
      inputRange: [0, 1],
      outputRange: [(height + 100) * -1, -80]
    });

    const opacityButton = this.floatingForm.interpolate({
      inputRange: [0, 1],
      outputRange: [1, 0]
    });

    if (this.state.getCurrentLocation) {
      return <ActivityIndicator size={"small"} color={"black"} />;
    }

    return (
      <View style={styles.container}>
        <MapView
          style={styles.maps}
          initialRegion={{
            latitude: this.initialRegion.latitude,
            longitude: this.initialRegion.longitude,
            latitudeDelta: this.delta.latitudeDelta,
            longitudeDelta: this.delta.longitudeDelta
          }}
          region={{
            latitude: this.state.form.maps.latitude,
            longitude: this.state.form.maps.longitude,
            latitudeDelta: this.delta.latitudeDelta,
            longitudeDelta: this.delta.longitudeDelta
          }}
          provider={MapView.PROVIDER_GOOGLE}
        >
          <MapView.Marker
            coordinate={{
              latitude: this.state.form.maps.latitude,
              longitude: this.state.form.maps.longitude
            }}
          />
        </MapView>
        <View style={styles.search}>
          <SearchLocationButton
            search={MarkerLang.searchYourLocation}
            onPress={this.navigateToSearchLocation}
          />
        </View>
        <Animated.View
          style={[styles.buttonFloating, { opacity: opacityButton }]}
        >
          <Button
            raised
            onPress={this.toggleForm}
            style={styles.button}
            color={"#3F51B5"}
          >
            <Text style={styles.labelButton}>
              {MarkerLang.markerPosition.toUpperCase()}
            </Text>
          </Button>
        </Animated.View>
        <Animated.ScrollView style={[styles.form, { bottom: bottomForm }]}>
          <TouchableWithoutFeedback onPress={this.toggleForm}>
            <View style={styles.wrapperExpandIcon}>
              <View style={styles.expandIcon} />
            </View>
          </TouchableWithoutFeedback>
          <Animated.View style={styles.wrapperTitle}>
            <FormMarker
              form={this.state.form}
              onSubmit={this.onSubmit}
              onImageAdd={this.onImageAdd}
              onRemoveImage={this.onRemoveImage}
              onChangeTextForm={this.onChangeTextForm}
              loading={this.state.loading}
            />
          </Animated.View>
        </Animated.ScrollView>
      </View>
    );
  }
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    ...Platform.select({
      ios: {},
      android: {
        marginTop: 24
      }
    })
  },
  maps: {
    position: "absolute",
    left: 0,
    right: 0,
    bottom: 0,
    top: 0,
    zIndex: 1
  },
  search: {
    position: "absolute",
    top: 20,
    left: 25,
    right: 25,
    height: 40,
    zIndex: 3,
    backgroundColor: "white",
    borderRadius: 5,
    elevation: 10
  },
  buttonFloating: {
    position: "absolute",
    bottom: 25,
    left: 25,
    right: 25,
    zIndex: 3
  },
  button: {
    borderRadius: 15
  },
  labelButton: {
    color: "white",
    fontWeight: "700"
  },
  form: {
    position: "absolute",
    left: 25,
    right: 25,
    backgroundColor: "white",
    zIndex: 5,
    borderTopLeftRadius: 5,
    borderTopRightRadius: 5,
    elevation: 20,
    height: height - 80
  },
  wrapperExpandIcon: {
    height: 20,
    justifyContent: "center",
    alignItems: "center"
  },
  expandIcon: {
    width: 30,
    height: 5,
    borderRadius: 5,
    backgroundColor: "#ebecf2"
  },
  buttonExpand: {
    position: "absolute",
    left: 0,
    right: 0,
    top: 0,
    height: 30,
    bottom: 0,
    backgroundColor: "black"
  }
});

MarkerScreen.propTypes = {
  submitForm: PropTypes.func.isRequired
};

export default connect(
  null,
  {
    submitForm
  }
)(MarkerScreen);
